import React from 'react';
import { Button } from 'reactstrap';
import { Notes } from './Synthesizer';
import synthesizer from './Synthesizer';
import { KeyboardEvent } from "react";


class Content extends React.Component {

  public play = (note: number) => {
    synthesizer.play(note, 0.2);
  };
  public playNotes = (event:React.KeyboardEvent<KeyboardEvent>)=>{
    if(event.key === 'Enter'){
      alert('enter press here!');
    }
  };

  public playNotes1 = ({e}: { e: any })=>{
    if(e===79){
      alert("You pressed o!");
    }
    else{alert(e.l);}

  };

  public render() {

    return (
        <div tabIndex={0} className="test" onKeyPress={(e) => this.playNotes1({e: e})}>
          {Object.keys(Notes).map(note => {
          return (
            <Button onClick={() => this.play(Notes[note])}>{note}</Button>
          );
        })}
        </div>

    )
  }
}

export default Content;
