import React, {useState} from 'react';
import {Button} from 'reactstrap';
import {Notes} from './Synthesizer';
import synthesizer from './Synthesizer';
import {KeyboardEvent} from "react";



var i = 0;
var z = 0;
var octavZahl=1;

interface IState {
  volume: number,
  sound: string,
  num: number

}
interface IProps {
  Sound: string[];
  //soundArr: 'sine' | 'triangle' | 'piano';
  //soundArr:'sine';
}

class Content extends React.Component<IProps, IState> {
  constructor (props:any){
    super(props);
    this.state = {
      sound: "sine",
      volume: 30,
      num: 0
    }
  }
  public getSound(){
    return (this.props.Sound[i]);
  }
  public play = (note: number) => {

    synthesizer.play(note, this.props.Sound[i], this.state.num);
  };
  public playNotes = (event: React.KeyboardEvent<KeyboardEvent>) => {
    if (event.key === 'Enter') {
      alert('enter press here!');
    }
  };

  public playNotes1 = ({e}: { e: any }) => {
    if (z>1){
      z=0;
    }
    else
    {z++;}



    if (e.keyCode === 65) {
      synthesizer.play(263.63, this.props.Sound[i], z);
    }
    else if (e.keyCode === 87) {
      synthesizer.play(277.18, this.props.Sound[i], z);
    }
    else if (e.keyCode === 83) {
      synthesizer.play(293.66, this.props.Sound[i], z);
    }
    else if (e.keyCode === 69) {
      synthesizer.play(311.13, this.props.Sound[i], z);
    }
    else if (e.keyCode === 68) {
      synthesizer.play(329.63, this.props.Sound[i], z);
    }
    else if (e.keyCode === 70) {
      synthesizer.play(349.23, this.props.Sound[i], z);
    }
    else if (e.keyCode === 84) {
      synthesizer.play(369.99, this.props.Sound[i], z);
    }
    else if (e.keyCode === 71) {
      synthesizer.play(392, this.props.Sound[i], z);
    }
    else if (e.keyCode === 90) {
      synthesizer.play(415.3, this.props.Sound[i], z);
    }
    else if (e.keyCode === 72) {
      synthesizer.play(440, this.props.Sound[i], z);
    }
    else if (e.keyCode === 85) {
      synthesizer.play(466.16, this.props.Sound[i], z);
    }
    else if (e.keyCode === 74) {
      synthesizer.play(493.88, this.props.Sound[i], z);
    }
    else if (e.keyCode === 75) {
      synthesizer.play(261.63 * 2, this.props.Sound[i], z);
    }


    else if(e.keyCode === 38) {
      synthesizer.volumePlus()
    }
    else if(e.keyCode === 40) {
      synthesizer.volumeMinus()
    }

    else if(e.keyCode ===39){

      if (i>2){
        i=0;
      }
      else
        {i++;}
      this.setState({
        sound: this.props.Sound[i]
      })

    }
    else if(e.keyCode ===37){

      if (i===0){
        i=3;
      }
      else
      {i--;}
      this.setState({
        sound: this.props.Sound[i]
      })

    }
    console.log(i + this.props.Sound[i] + e.keyCode);
    this.setState({
      volume: Math.round(synthesizer.getGainNode()*100)

      }
    )

  };



  /*public stopNotes = ({e}: {e: any})=>{
   synthesizer.noteStop();
  };*/
  public octaveHoch = () =>{
    octavZahl=octavZahl*2;
    synthesizer.setOctavmtpl(octavZahl);
  };
  public octaveRunter = () =>{
    octavZahl=octavZahl/2;
    synthesizer.setOctavmtpl(octavZahl);
  };



  public render() {

    return (
      <div tabIndex={0} className="test" onKeyDown={(e) => this.playNotes1({e: e})}>
        <div>
          <div>Um die Tastatur zu benutzen klicke auf die Seite!</div>
          <p> <button onClick={()=>this.octaveHoch()}>Oktave++</button>
                    <button onClick={()=>this.octaveRunter()}>Oktave--</button></p>
          <p>Um die Lautstärke und den Sound einzustellen benutze die Pfeiltasten!</p>
          <p>Volume:  <span className="hervorgehoben">{this.state.volume}</span>% </p>
          <p>Sound: <span className="hervorgehoben">{i+1+". "+this.state.sound}</span></p>
        </div>
        {Object.keys(Notes).map(note => {
          return (
            <Button onClick={() => this.play(Notes[note])}>{note}</Button>
          );

        })}
        <p></p>
        <div>Die <span className="hervorgehoben">ganzen Noten</span> liegen auf den Buchstaben: <span className="hervorgehoben">a s d f g h j k</span> </div>
        <div><p>Die <span className="hervorgehoben">halbtonschritte dazwischen</span> liegen auf den Buchstaben: <span className="hervorgehoben">w e t z u</span></p>
      </div>
      </div>
    )
  }
}

export default Content;
