/*
https://pages.mtu.edu/~suits/notefreqs.html
*/


// 1 = loud
var VOLUME = 0.3;
var i = 0;

// work around typescript warnings
const wnd = window as any;

export const Notes: {
  [key: string]: number
} = {
  C: 261.63,
  Cs: 277.18,
  D: 293.66,
  Ds: 311.13,
  E: 329.63,
  F: 349.23,
  Fs: 369.99,
  G: 392,
  Gs: 415.30,
  A: 440,
  As: 466.16,
  B: 493.88,
  C2: 261.63 * 2
};

class Synthesizer {

  private audioCtx: AudioContext = new (wnd['AudioContext'] || wnd['webkitAudioContext'])();
  private gainNode: GainNode;


  constructor() {

    this.gainNode = this.audioCtx.createGain();
    // connect to audio context
    this.gainNode.connect(this.audioCtx.destination);
    // lower volume
    this.gainNode.gain.value = VOLUME;
    //this.gainNode.gain.setValueAtTime(0.3, this.audioCtx.currentTime);
    this.gainNode.gain.setValueAtTime(VOLUME, this.audioCtx.currentTime + 0.5);
    //this.gainNode.gain.exponentialRampToValueAtTime(0.1, this.audioCtx.currentTime + 0.5);

  }
  public getGainNode(){
    return (this.gainNode.gain.value);
  }

  public volumePlus () {
    if(VOLUME<0.70){
    VOLUME = VOLUME + 0.01;}
    else{
      alert("max erreicht");
      VOLUME = 0.70;
    }
    this.gainNode.gain.value=VOLUME;
    //this.gainNode.gain.setValueAtTime(VOLUME, this.audioCtx.currentTime + 0.5);
    console.log("Vol: "+VOLUME ,"Gain: "+this.gainNode.gain.value);
  }
  public volumeMinus () {
    if(this.gainNode.gain.value>0.01){
    this.gainNode.gain.value = this.gainNode.gain.value - 0.01;}
    else{
      this.gainNode.gain.value=0;
    }
    console.log(this.gainNode.gain.value);
  }
  public play(note: number, type: any) {

    const oscillator = this.audioCtx.createOscillator();
    oscillator.connect(this.gainNode);

    // default
    oscillator.type = type;
    oscillator.frequency.value = note;

    oscillator.start(0);
    //oscillator.frequency.setValueAtTime(1, this.audioCtx.currentTime + 2 );
    //oscillator.frequency.setValueAtTime(0, this.audioCtx.currentTime + 2 );
    //oscillator.frequency.exponentialRampToValueAtTime(0.01,this.audioCtx.currentTime + 0.5 );
    oscillator.stop(this.audioCtx.currentTime + 0.5);
  }

  public stop() {

  }

}

const synthesizer = new Synthesizer();
export default synthesizer;
