// 1 = loud
var VOLUME = 0.3;
var i = 0;


// work around typescript warnings
const wnd = window as any;

export const Notes: {
  [key: string]: number
} = {
  C: 261.63,
  Cs: 277.18,
  D: 293.66,
  Ds: 311.13,
  E: 329.63,
  F: 349.23,
  Fs: 369.99,
  G: 392,
  Gs: 415.30,
  A: 440,
  As: 466.16,
  B: 493.88,
  C2: 261.63 * 2
};

class Synthesizer {

  private audioCtx: AudioContext = new (wnd['AudioContext'] || wnd['webkitAudioContext'])();
  private gainNode: GainNode;



  constructor() {

    this.gainNode = this.audioCtx.createGain();
    // connect to audio context
    this.gainNode.connect(this.audioCtx.destination);
    // lower volume
    //this.gainNode.gain.value = VOLUME;
    //this.gainNode.gain.setValueAtTime(0.3, this.audioCtx.currentTime);

    //this.gainNode.gain.exponentialRampToValueAtTime(0.1, this.audioCtx.currentTime + 0.5);

  }
  public getGainNode(){
    var volume = VOLUME;
    return volume;
  }

  public volumePlus () {
    if(VOLUME<1){
    VOLUME = VOLUME + 0.01;}
    else{
      VOLUME = 1;
    }
    this.gainNode.gain.value=VOLUME/6;
    console.log("Vol: "+VOLUME ,"Gain: "+this.gainNode.gain.value);
  }
  public volumeMinus () {
    if(VOLUME>0.01){
    VOLUME = VOLUME - 0.01;}
    else{
      VOLUME=0;
    }
    this.gainNode.gain.value=VOLUME/6;
    console.log(this.gainNode.gain.value);
  }

  public play(note: number, type: any) {

    const oscillator = this.audioCtx.createOscillator();
    var off = false;
    off = this.gainNode.gain.value < 0.01;

    oscillator.connect(this.gainNode);

    // default
    oscillator.type = type;
    oscillator.frequency.value = note;


    this.gainNode.gain.setValueAtTime(VOLUME/6, this.audioCtx.currentTime );
    //this.gainNode.gain.exponentialRampToValueAtTime(VOLUME, this.audioCtx.currentTime +0.5);
    //this.gainNode.gain.exponentialRampToValueAtTime(0.001, this.audioCtx.currentTime+0.5);
    this.gainNode.gain.linearRampToValueAtTime(0.0, this.audioCtx.currentTime+0.3);
    //this.gainNode.gain.setValueAtTime(0, this.audioCtx.currentTime + 1);
    //oscillator.frequency.setValueAtTime(1, this.audioCtx.currentTime + 2 );
    //oscillator.frequency.setValueAtTime(0, this.audioCtx.currentTime + 2 );
    oscillator.frequency.exponentialRampToValueAtTime(note,this.audioCtx.currentTime + 0.3 );
    oscillator.start();
    oscillator.stop(this.audioCtx.currentTime + 0.3 );


  }

  public stop() {

  }

}

const synthesizer = new Synthesizer();
export default synthesizer;
