    }
    if(e.keyCode===83){
      synthesizer.play(293.66);
    }
    if(e.keyCode===68){
      synthesizer.play(329.63);
    }
    if(e.keyCode===70){
      synthesizer.play(349.23);
    }if(e.keyCode===71){
      synthesizer.play(392);
    }if(e.keyCode===72){
      synthesizer.play(440);
    }if(e.keyCode===74){
      synthesizer.play(493.88);
    }if(e.keyCode===75){
      synthesizer.play(261.63*2);
    }
    console.log(e.keyCode);




  };
  public myTimer(){
    var d=new Date();
  }
  /*public stopNotes = ({e}: {e: any})=>{
   synthesizer.noteStop();
  };*/

  public render() {

    return (
        <div tabIndex={0} className="test" onKeyDown={(e) => this.playNotes1({e: e})} /*onKeyUp={(e) => this.stopNotes({e: e})}*/>
          {Object.keys(Notes).map(note => {
          return (
            <Button onClick={() => this.play(Notes[note])}>{note}</Button>
          );
        })}
        </div>

    )
  }
}

export default Content;
